let headers = {
  "X-Auth-Token" : "4fb1d2a25bbd47b8b0e198981893128b"
}

new Vue({
  el: "#area",
  data: {
    response: null
  },

  mounted() {
    axios({
      url: "http://api.football-data.org/v2/areas",
      method: "get",
      responseType: "stream",
      headers : headers
    })

    .then(response => {
      let data = response.data.areas;
      console.log(data);
      this.response = data;
    })
    .catch(error => {
      console.log(error);
    });
  }
});